+import React from "react";
+import { Link, useNavigate } from "react-router-dom";
+import styled from "styled-components";
+import TypeIt from "typeit-react";
+
+export const MainHome = () => {
+  const navigate = useNavigate();
+  return (
+    <div className="w-[80%] h-[100%] flex flex-col justify-center items-center gap-3 box-border font-bold text-2xl bg-[#998200]">
+      <TypeIt>안녕하세요. 저의 Portfolio에 오신걸 환영합니다. 아래 버튼을 눌러 로그인을 해주세요.</TypeIt>
+      <TypeIt className="text-center"
+      getBeforeInit={(instance) => {
+        instance
+          .type("안녕하세요.")
+          .break()
+          .pause(500)
+          .type("저의 Portfolio에 오신걸 환영합니다.")
+          .break()
+          .pause(500)
+          .type("아래 버튼을 눌러 로그인을 해주세요.");
+        return instance;
+      }}
+    />
+      <div className="btn btn-primary bg-[#333333a2] w-[100px] h-[40px] rounded-full hover:bg-[#49494988] flex justify-center items-center mb-3">
+        <button className="btn btn-primary" onClick={() => navigate("/login")}>로그인</button>
+      </div>
+
+    </div>
+  );
+};
+
+export const Home = styled("div")`
+  box-sizing: border-box;
+  display: flex;
+  flex-direction: column;
+  justify-content: center;
+  align-items: center;
+`;

