+              // </span>
+              <div key={idx} className="flex items-center gap-1 bg-[#a8a8a82a] rounded-xl">
+                {techIcons[tech] && techIcons[tech]}
+                <span className="text-sm">{tech}</span>
+              </div>
+            ))}
+          </div>
+          <div className="mt-auto w-auto">
+            <Button
+              variant="outlined"
+              size="small"
+              className="mt-4"
